import { Action } from 'redux';
import { ThunkAction } from 'redux-thunk';

import * as actions from './index';
import { State } from '../reducers';

function exampleAPI() {
  return Promise.resolve('Async Chat Bot');
}


export const thunkSendMessage = (
  message: string,
): ThunkAction<void, State, unknown, Action<string>> => async (dispatch) => {
//   const asyncResp = await exampleAPI();
  const planetFetch =await fetch('https://findfalcone.herokuapp.com/planets');
  const planetdata = await planetFetch.json();
  dispatch(actions.planetAction(planetdata));
//   dispatch(actions.sampleAction(asyncResp));
};
