import  {Planet, PlanetAction, ReduxActions} from '../actions/index'
import * as actionTypes from '../actions/types'


export const initialState:Planet ={
    name,
    distance:0
}

const Reducer = (state:Planet=initialState,action:ReduxActions) => {
    switch(action.type){
    case actionTypes.PLANET_ACTION : return {
        ...state,
        name:action.payload.map(items => items.name),
        distance:action.payload.map(items => items.distance)

    }
    default :return state
}
}
export default Reducer;
