import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import * as actions from '../actions';
import {
  thunkSendMessage,
  planetsApi,
  VehiclesApi,
  GetTokenFromApi,
} from '../actions/thunkActions';
import logo from '../logo.svg';
import './App.css';
import { State } from '../reducers';

const Selection = () => {
  const message = useSelector((state: State) => state.message);
  const id = useSelector((state: State) => state.id);
  const planet = useSelector((state: State) => state.planet);
  const vehicle = useSelector((state: State) => state.vehicle);
  const dispatch = useDispatch();

  const [planets, setPlanets] = useState <Array<string>>([]);

  useEffect(() => {
    dispatch(GetTokenFromApi());
    dispatch(planetsApi());
    dispatch(VehiclesApi());
  }, []);

  const onClick = () => {
    dispatch(actions.sampleAction2(id + 1));
    dispatch(thunkSendMessage('hello'));
  };

  // const showPlanets = () => {
  //   // const selectedPlanet = event.currentTarget.value;
  //   // setPlanets([...planets, selectedPlanet]);
  //   // const planetList = planet.filter((data) => !planets.includes(data.name));
  //   const list = planet.map((data) => (
  //     <>
  //       <option id={data.name} value={data.name}>{data.name}</option>
  //     </>

  //   ));
  //   return list;
  // };
  const onSelectPlanet = (event:React.ChangeEvent<HTMLSelectElement>) => {
    const selectedPlanet = event.currentTarget.value;
    setPlanets([...planets, selectedPlanet]);
    console.log(planets[0]);
  };
  const showPlanets2 = () => {
    // const selectedPlanet = event.currentTarget.value;
    // setPlanets([...planets, selectedPlanet]);
    // console.log(planets);
    const planetList = planet.filter((data) => !planets.includes(data.name));
    const list = planetList.map((data) => (
      <>
        <option id={data.name}>{data.name}</option>
      </>

    ));
    return list;
  };


  return (
    <div>
      <div className="App">
        <header className="App-header">
          message:
          {' '}
          {message}
          <img src={logo} className="App-logo" alt="logo" />
          id:
          {' '}
          {id}
          <button type="button" onClick={onClick}>
            increment id
          </button>
          <div className="row">
            <div className="col">
              <select value={planets[0]} onChange={onSelectPlanet}>
  <option selected>{planets[0]}</option>

                {showPlanets2()}
              </select>

            </div>
            <div className="col">
              <select onChange={onSelectPlanet}>
                <option selected>{planets[1]}</option>
                {showPlanets2()}
              </select>
            </div>
            <div className="col" />
            <div className="col" />
          </div>
        </header>
      </div>
    </div>
  );
};
export default Selection;
