import  {Planet, PlanetAction, ReduxActions} from '../actions/index'
import * as actionTypes from '../actions/types'


export interface AppState {
    planet:Planet[],
}
export const initialState:AppState ={
    planet:[]

}

const Reducer = (state:Planet[]=initialState.planet,action:ReduxActions) => {
    switch(action.type){
    case actionTypes.PLANET_ACTION : 
    return  {
        name:[...state,action.payload]
    }

    default :return state
}
}
export default Reducer;
