import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import * as actions from "../actions";
import {
  thunkSendMessage,
  planetsApi,
  VehiclesApi,
  GetTokenFromApi,
} from "../actions/thunkActions";
import logo from "../logo.svg";
import "./App.css";
import { State } from "../reducers";
// import thunkSendMessage from '../actions/thunkActions'

const App = () => {
  useEffect(() => {
    dispatch(GetTokenFromApi());
    dispatch(planetsApi());
    dispatch(VehiclesApi());
  }, []);

const [planetArray,setPlanetArray] = useState<Array<string>>([]);


  const message = useSelector((state: State) => state.message);
  const id = useSelector((state: State) => state.id);
  const planet = useSelector((state: State) => state.planet);
  const vehicle = useSelector((state: State) => state.vehicle);
  const dispatch = useDispatch();

  const onClick = () => {
    dispatch(actions.sampleAction2(id + 1));
    dispatch(thunkSendMessage("hello"));
  };

const planetNameIntoArray = (event:  React.ChangeEvent<HTMLSelectElement>) => {
  setPlanetArray(oldArray=>[...oldArray,event.currentTarget.value])
  console.log("onchangefilter",event.currentTarget.value)
}

console.log(planetArray);

  return (
    <div className='App'>
      <header className='App-header'>
        message: {message}
        <img src={logo} className='App-logo' alt='logo' />
        id: {id}
        <button type='button' onClick={onClick}>
          increment id
        </button>
        <div className='row'>
          <div className='col-sm'>
            <div className='form-group'>
              <select onChange={planetNameIntoArray}className='form-control' id='exampleFormControlSelect1'>
                <option> </option>
                {planet.map((planets) => (
                  <option value={planets.name}  >{planets.name} </option>
                ))}
              </select>
            </div>
          </div>
          <div className='col-sm'>
            <div className='form-group'>
              <select onChange={planetNameIntoArray} className='form-control' id='exampleFormControlSelect1'>
                <option> </option>
                {planet.map((planets) => (
                  <option value={planets.name}>{planets.name} </option>
                ))}
              </select>
            </div>
          </div>
          <div className='col-sm'>
            <div className='form-group'>
              <select onChange={planetNameIntoArray} className='form-control' id='exampleFormControlSelect1'>
                <option> </option>
                {planet.map((planets) => (
                  <option value={planets.name}>{planets.name} </option>
                ))}
              </select>
            </div>
          </div>
          <div className='col-sm'>
            <div className='form-group'>
              <select onChange={planetNameIntoArray} className='form-control' id='exampleFormControlSelect1'>
                <option> </option>
                {planet.map((planets) => (
                  <option value={planets.name}>{planets.name} </option>
                ))}
              </select>
            </div>
          </div>
        </div>
        {planet.map((planets) => (
          <h1>
            {planets.name} {planets.distance}
          </h1>
        ))}
        {vehicle.map((planets) => (
          <h1>
            {planets.name} {planets.total_no} {planets.max_distance}{" "}
            {planets.speed}{" "}
          </h1>
        ))}
      </header>
    </div>
  );
};

export default App;
