import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import * as actions from '../actions';
import {
  thunkSendMessage,
  planetsApi,
  VehiclesApi,
  GetTokenFromApi,
} from '../actions/thunkActions';
import logo from '../logo.svg';
import './App.css';
import { State } from '../reducers';
import { Planet } from '../types/types';
import Selection from './Selection';
// import thunkSendMessage from '../actions/thunkActions'

const App = () => {
  const [option1, setOption1] = useState<string>('');
  const [option2, setOption2] = useState<string>('');
  const [option3, setOption3] = useState<string>('');
  const [option4, setOption4] = useState<string>('');


  const message = useSelector((state: State) => state.message);
  const id = useSelector((state: State) => state.id);
  const planet = useSelector((state: State) => state.planet);
  const vehicle = useSelector((state: State) => state.vehicle);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(GetTokenFromApi());
    dispatch(planetsApi());
    dispatch(VehiclesApi());
  }, []);

  const onClick = () => {
    dispatch(actions.sampleAction2(id + 1));
    dispatch(thunkSendMessage('hello'));
  };


  const planetNameintooption1 = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const data = event.currentTarget.value;
    setOption1(data);
  };
  const planetNameintooption2 = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const data = event.currentTarget.value;
    setOption2(data);
  };
  const planetNameintooption3 = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const data = event.currentTarget.value;
    setOption3(data);
  };
  const planetNameintooption4 = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const data = event.currentTarget.value;
    setOption4(data);
  };
  const unionPlanets:Array<string> = [option1, option2, option3, option4];
  console.log(unionPlanets);

  const planetFilter = () => {
    const filter = planet.filter((data) => data.name !== option2 && data.name !== option3 && data.name !== option4);
    const list = filter.map((data) => (
      <>
        <option value={data.name}>
          {data.name}
          {' '}
          (
          {data.distance}
          )
        </option>

      </>
    ));
    return list;
  };
  const planetFilter2 = () => {
    const filter = planet.filter((data) => data.name !== option1 && data.name !== option3 && data.name !== option4);
    const list = filter.map((data) => (
      <>
        <option value={data.name}>
          {data.name}
          {' '}
          (
          {data.distance}
          )

        </option>

      </>
    ));
    return list;
  };
  const planetFilter3 = () => {
    const filter = planet.filter((data) => data.name !== option1 && data.name !== option2 && data.name !== option4);
    const list = filter.map((data) => (
      <>
        <option>
          {data.name}
          {' '}
          (
          {data.distance}
          )

        </option>

      </>
    ));
    return list;
  };
  const planetFilter4 = () => {
    const filter = planet.filter((data) => data.name !== option1 && data.name !== option2 && data.name !== option3);
    const list = filter.map((data) => (
      <>
        <option>
          {data.name}
          {' '}
          (
          {data.distance}
          )
        </option>

      </>
    ));
    return list;
  };

  const vehicleFilter = (planetName) => {
    const planetData = planet.filter((data) => data.name === planetName);
    const distanceData:number = Number((planetData.map((data) => data.distance)));
    console.log(distanceData);
    // planetData.map((data) => console.log(data.distance));
    const list = vehicle.filter((data) => data.max_distance >= distanceData);

    const vehicleList = list.map((data) => (
      <>
        <div className="form-check">
          <input className="form-check-input" type="radio" name="exampleRadios" id="exampleRadios1" value={data.name} />
          <label className="form-check-label" htmlFor="exampleRadios1">
            {data.name}
            (
            {data.total_no}
            )
          </label>
          {/* <h1>{data.name}</h1> */}

        </div>
      </>
    ));
    return vehicleList;
  };


  return (
    <div className="App">
      <header className="App-header">
        message:
        {' '}
        {message}
        <img src={logo} className="App-logo" alt="logo" />
        id:
        {' '}
        {id}
        <button type="button" onClick={onClick}>
          increment id
        </button>
        <div className="row">
          <div className="col-sm">
            {option1}
            <div className="form-group">
              <select onChange={planetNameintooption1} className="form-control" id="exampleFormControlSelect1">
                <option> </option>
                {/* {planet.map((planets) => (
                  <option value={planets.name}>
                    {planets.name}
                    {' '}
                  </option>
                ))} */}
                {planetFilter()}


              </select>
              {vehicleFilter(option1)}
            </div>
          </div>
          <div className="col-sm">
            {option2}
            <div className="form-group">
              <select onChange={planetNameintooption2} className="form-control" id="exampleFormControlSelect1">
                <option> </option>
                {/* {planet.map((planets) => (
                  <option value={planets.name}>
                    {planets.name}
                    {' '}
                  </option>
                ))} */}
                {planetFilter2()}
              </select>
              {vehicleFilter(option2)}
            </div>
          </div>
          <div className="col-sm">
            {option3}
            <div className="form-group">
              <select onChange={planetNameintooption3} className="form-control" id="exampleFormControlSelect1">
                <option> </option>
                {/* {planet.map((planets) => (
                  <option value={planets.name}>
                    {planets.name}
                    {' '}
                  </option>
                ))} */}
                {planetFilter3()}
              </select>
              {vehicleFilter(option3)}
            </div>
          </div>
          <div className="col-sm">
            {option4}
            <div className="form-group">
              <select onChange={planetNameintooption4} className="form-control" id="exampleFormControlSelect1">
                <option> </option>
                {/* {planet.map((planets) => (
                  <option value={planets.name}>
                    {planets.name}
                    {' '}
                  </option>
                ))} */}
                {planetFilter4()}
              </select>
              {vehicleFilter(option4)}
            </div>
          </div>
        </div>


        {/* {planet.map((planets) => (
          <h1>
            {planets.name}
            {' '}
            {planets.distance}
          </h1>
        ))} */}
        {vehicle.map((planets) => (
          <h1>
            {planets.name}
            {' '}
            {planets.max_distance}
            {' '}
            {planets.speed}
            {' '}
          </h1>
        ))}

      </header>
      <Selection />
    </div>
  );
};

export default App;
