import * as actionTypes from "./types";

type SampleAction = {
  type: actionTypes.SAMPLE_ACTION;
  payload: string;
};
export const sampleAction = (payload: string): SampleAction => {
  return { type: actionTypes.SAMPLE_ACTION, payload };
};

type SampleAction2 = {
  type: actionTypes.SAMPLE_ACTION2;
  payload: number;
};
export const sampleAction2 = (payload: number): SampleAction2 => {
  return { type: actionTypes.SAMPLE_ACTION2, payload };
};

export type ReduxActions = SampleAction | SampleAction2;

// export * from "./thunkActions"
