import React, { useEffect, useState, ChangeEvent } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  Select, Radio, Layout, Button,
} from 'antd';
import { RadioChangeEvent } from 'antd/lib/radio/interface';
import {
  thunkSendMessage,
  planetsApi,
  VehiclesApi,
  GetTokenFromApi,
} from '../actions/thunkActions';


import logo from './Logo.png';
import './App.css';
import { State } from '../reducers';
import { Planet, Vehicle } from '../types/types';


const { Header, Content, Footer } = Layout;
const { Option } = Select;

const radioStyle = {
  display: 'block',
  height: '30px',
  lineHeight: '30px',
};
const Selection = () => {
  // const message = useSelector((state: State) => state.message);
  // const id = useSelector((state: State) => state.id);
  const allPlanets = useSelector((state: State) => state.planet);
  const allVehicles = useSelector((state: State) => state.vehicle);
  const token = useSelector((state:State) => state.token);
  const dispatch = useDispatch();

  const [selectedPlanets, setPlanets] = useState<Array<string>>([]);
  const [planetArray, setArray] = useState<Planet[]>([]);
  const [selectedVehicle, setVehicle] = useState <Array<string>>([]);
  const [vehicleArray, setVehicleArray] = useState <Vehicle[]>([]);
  const [time, setTime] = useState <number>(0);

  useEffect(() => {
    dispatch(GetTokenFromApi());
    dispatch(planetsApi());
    dispatch(VehiclesApi());
  }, []);

  // const onClick = () => {
  //   dispatch(actions.sampleAction2(id + 1));
  //   dispatch(thunkSendMessage("hello"));
  // };


  const onSelectPlanet = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedPlanet = event.currentTarget.value;
    const planetDistance = allPlanets.filter(
      (planet) => planet.name === selectedPlanet,
    );
    const index = Number(event.currentTarget.id);
    planetArray[index] = planetDistance[0];
    setArray([...planetArray]);
    const vehilceList = listVehiclesAt(index);
    selectedPlanets[index] = selectedPlanet;
    setPlanets([...selectedPlanets]);
    return vehilceList;
  };

  const listPlanetOptionsAt = (currentIndex: number) => {
    const planetList = allPlanets.filter(
      (planet) => selectedPlanets[currentIndex] === planet.name
        || !selectedPlanets.includes(planet.name),
    );
    const list = planetList.map((data, index) => (
      <option value={data.name}>{data.name}</option>
    ));


    return list;
  };


  const listVehiclesAt = (currentIndex: number) => {
    const { distance } = planetArray[currentIndex];

    // const vehicleList = allVehicles.filter(
    //   (vehicle) => vehicle.max_distance >= distance,
    // );
    const vehicleOptions = allVehicles.map((vehicle) => (
      vehicle.max_distance >= distance && vehicle.total_no > 0 ? (
        <Radio style={radioStyle} value={vehicle.name}>
          {vehicle.name}
          (
          {vehicle.total_no}
          )
        </Radio>
      )
        : (
          <Radio style={radioStyle} value={vehicle.name} disabled>
            {vehicle.name}
            (
            {vehicle.total_no}
            )
          </Radio>
        )
    ));
    return vehicleOptions;
  };

  const listVehicles = () => {
    const vehicleOptions = allVehicles.map((vehicle) => (
      <>
        <Radio style={radioStyle} value={vehicle.name}>
          {vehicle.name}
          (
          {vehicle.total_no}
          )
        </Radio>
      </>
    ));
    return vehicleOptions;
  };


  const vehicleOnChange = (e:RadioChangeEvent) => {
    const optionSelected = e.target.value;
    const allSelectedPlanets = allVehicles.filter(
      (vehicle) => vehicle.name === optionSelected,
    );
    const currentIndex = Number(e.target.name);
    vehicleArray[currentIndex] = allSelectedPlanets[0];
    setVehicleArray([...vehicleArray]);
    console.log('selected Vehicles ::', vehicleArray);
    const prevOption = selectedVehicle[currentIndex];
    selectedVehicle[currentIndex] = optionSelected;
    setVehicle([...selectedVehicle]);
    if (prevOption != optionSelected && prevOption != undefined) {
      const indexOfVehicle = allVehicles.map((e) => e.name).indexOf(prevOption);
      allVehicles[indexOfVehicle].total_no = allVehicles[indexOfVehicle].total_no + 1;
      const indexOfVehicle2 = allVehicles.map((e) => e.name).indexOf(optionSelected);
      allVehicles[indexOfVehicle2].total_no = allVehicles[indexOfVehicle2].total_no - 1;
    } else {
      const indexOfVehicle = allVehicles.map((e) => e.name).indexOf(optionSelected);
      allVehicles[indexOfVehicle].total_no = allVehicles[indexOfVehicle].total_no - 1;
    }
  };

  return (
    <div>
      <div className="falcone-main-layout">
        <Layout className="layout ">
          <Header>
            <div className="logo">
              <img className="falcone-logo" src={logo} alt="falcone-logo" />
              <h2 className="finding-falcone">FINDING FALCONE</h2>
            </div>
          </Header>
          <Content style={{ padding: '0 50px' }}>
            <div className="site-layout-content falcone-row-primary">
              <h5 className="falcone-text text-center">Select 4 planets and the space vehicles to search in these planets</h5>
              <p className="float-left">
                TIME TAKEN:
                {time}
              </p>
              <br />
              <br />
              <div className="row">
                <div className="col">
                  <h5 className="falcone-planet-h5">Planet 1 </h5>
                  <h6 className="falcone-selectPlanets">Select the planet </h6>
                  <select
                    className="falcone-planet-select"
                    id="0"
                    value={selectedPlanets[0]}
                    onChange={onSelectPlanet}
                  >
                    <option />

                    {listPlanetOptionsAt(0)}
                  </select>
                  {/* <Select value={selectedPlanets[0]} style={{ width: 120 }} onChange={handleChange}>
                    <Option value=" "> </Option>
                    {listPlanetOptionsAt(0)}
                  </Select> */}
                  <div>
                    <h6 className="falcone-selectSpaceShip">Select the space ship </h6>

                    {selectedPlanets[0] === undefined
                      ? (
                        <Radio.Group name="0" onChange={vehicleOnChange}>
                          {' '}
                          {listVehicles()}
                        </Radio.Group>
                      )
                      : (
                        <Radio.Group name="0" onChange={vehicleOnChange}>
                          {' '}
                          {listVehiclesAt(0)}
                        </Radio.Group>
                      )}
                  </div>
                  {/* {
                    selectedPlanets[0] === undefined ? "" :
                     <Radio.Group  name="0" onChange={vehicleOnChange} > {listVehiclesAt(0)}
                  </Radio.Group>
                }
                 */}

