import  {Planet, PlanetAction, ReduxActions} from '../actions/index'
import * as actionTypes from '../actions/types'



const reducer = (state:Planet[]=[],action:ReduxActions) => {
    switch(action.type){
    case actionTypes.PLANET_ACTION : return {
        ...state,
        name:action.payload,
        distance:action.payload
    }
    default :return state
}
}
export default reducer;
