import { combineReducers } from 'redux';

import { ReduxActions } from '../actions';
import * as actionTypes from '../actions/types';
import { Planet, Vehicle, Result } from '../types/types';

export type State = {
  message: string;
  id: number;
  planet:Planet[];
  vehicle:Vehicle[];
  token:string;
  result:string | Result;
};

export default combineReducers<State, ReduxActions>({
  message: (state = '', action) => {
    switch (action.type) {
      case actionTypes.SAMPLE_ACTION:
        return action.payload;
      default:
        return state;
    }
  },
  id: (state = 0, action) => {
    switch (action.type) {
      case actionTypes.SAMPLE_ACTION2:
        return action.payload;
      default:
        return state;
    }
  },
  planet: (state = [], action) => {
    switch (action.type) {
      case actionTypes.SET_PLANET_TO_STORE:
        return action.payload;
      default:
        return state;
    }
  },
  vehicle: (state = [], action) => {
    switch (action.type) {
      case actionTypes.SET_VEHICLES_TO_STORE:
        return action.payload;
      default:
        return state;
    }
  },
  token: (state = '', action) => {
    switch (action.type) {
      case actionTypes.GET_TOKEN:
        return action.payload;
      default:
        return state;
    }
  },
  result: (state = '', action) => {
    switch (action.type) {
      case actionTypes.FIND_FALCON:
        return action.payload;
      default:
        return state;
    }
  },

});
