import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import * as actions from "../actions";
import {
  thunkSendMessage,
  planetsApi,
  VehiclesApi,
  GetTokenFromApi,
} from "../actions/thunkActions";
import logo from "../logo.svg";
import "./App.css";
import { State } from "../reducers";

const Selection = () => {
  const message = useSelector((state: State) => state.message);
  const id = useSelector((state: State) => state.id);
  const allPlanets = useSelector((state: State) => state.planet);
  const vehicle = useSelector((state: State) => state.vehicle);
  const dispatch = useDispatch();

  const [selectedPlanets, setPlanets] = useState<Array<string>>([]);

  useEffect(() => {
    dispatch(GetTokenFromApi());
    dispatch(planetsApi());
    dispatch(VehiclesApi());
  }, []);

  const onClick = () => {
    dispatch(actions.sampleAction2(id + 1));
    dispatch(thunkSendMessage("hello"));
  };

  // const showPlanets = () => {
  //   // const selectedPlanet = event.currentTarget.value;
  //   // setPlanets([...planets, selectedPlanet]);
  //   // const planetList = planet.filter((data) => !planets.includes(data.name));
  //   const list = planet.map((data) => (
  //     <>
  //       <option id={data.name} value={data.name}>{data.name}</option>
  //     </>

  //   ));
  //   return list;
  // };
  const onSelectPlanet = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedPlanet = event.currentTarget.value;
    const index = Number(event.currentTarget.id);
    console.log(index, selectedPlanet);
    selectedPlanets[index] = selectedPlanet;
    setPlanets([...selectedPlanets]);
    console.log("onSelectPlanet::", selectedPlanets);
    // const planetList = planet.filter((data) => !planets.includes(data.name));
    // console.log(planetList);
    // const list = planetList.map((data) => (
    //   <>
    //     <option id={data.name}>{data.name}</option>
    //   </>

    // ));
    // return list;
  };

  const showPlanets2 = () => {
    const planetList = allPlanets.filter(
      (planet) => !selectedPlanets.includes(planet.name)
    );
    const list = planetList.map((data, index) => 
        <option value={data.name}>{data.name}</option>
    );
    console.log("showPlanets::", planetList);
    return list;
  };

  return (
    <div>
      <div className='App'>
        <header className='App-header'>
          message: {message}
          <img src={logo} className='App-logo' alt='logo' />
          id: {id}
          <button type='button' onClick={onClick}>
            increment id
          </button>
          <div className='row'>
            <div className='col'>
              <select>
                <option value={"hello1"}>helo1</option>
                <option value={"hello2"}>helo2</option>
                <option value={"hello3"}>helo3</option>
                <option value={"hello4"}>helo4</option>
              </select>
              {/* <select id='0' value={selectedPlanets[0]} onChange={onSelectPlanet}> */}
                {/* <option selected>{selectedPlanets[0]}</option> */}

                {/* {showPlanets2()} */}
              {/* </select> */}
            </div>
            <div className='col'>
              <select value='' id='1' onChange={onSelectPlanet}>
                {/* <option selected>{selectedPlanets[1]}</option> */}
                {showPlanets2()}
              </select>
            </div>
            <div className='col'>
              <select id='2' onClick={showPlanets2} onChange={onSelectPlanet}>
                <option selected>{selectedPlanets[2]}</option>
                {showPlanets2()}
              </select>
            </div>
            <div className='col'>
              <select id='3' onClick={showPlanets2} onChange={onSelectPlanet}>
                <option selected>{selectedPlanets[3]}</option>
                {showPlanets2()}
              </select>
            </div>
          </div>
        </header>
      </div>
    </div>
  );
};
export default Selection;
