import React,{useEffect} from "react";

import "./App.css";
import { useSelector, useDispatch } from "react-redux";
import * as actions from "../actions";

// import { thunkSendMessage } from "../actions/thunkActions";
// import { State } from "../reducers";

const App = () => {
  useEffect(() =>{
    actions.planetAction
  },[]
  )
  const name = useSelector((state: actions.Planet) => state.name);
  const distance = useSelector((state: actions.Planet) => state.distance);
  const dispatch = useDispatch();

  return (
    <div className='App'>
      <select>
        {
          name
        }
        <option>{name}</option>
        <option>{distance}</option>
      </select>
    </div>
  );
};

export default App;
