import React, { useState } from 'react';
import { Select } from 'antd';
import { useSelector } from 'react-redux';
import { State } from '../reducers';
import { Planet } from '../types/types';

interface MyProps {
  index:number
}

const { Option } = Select;
const SelectPlanet:React.FunctionComponent<MyProps> = (props) => {
  const allPlanets = useSelector((state:State) => state.planet);
  const [selectedPlanets, setSelectedPlanets] = useState <Array<string>>([]);
  const [planetArray, setArray] = useState <Planet[]>([]);


  function handleChange(selectedPlanet) {
    const planetDistance = allPlanets.filter(
      (planet) => planet.name === selectedPlanet,
    );
    const { index } = props;
    planetArray[index] = planetDistance[0];
    setArray([...planetArray]);
    const vehilceList = listVehiclesAt(index);
    selectedPlanets[index] = selectedPlanet;
    setPlanets([...selectedPlanets]);
    console.log(`selected ${selectedPlanet}`);
    return vehilceList;
  }
  const planetOptions = () => {
    const { currentIndex } = props.index;
    const planetList = allPlanets.filter(
      (planet) => selectedPlanets[currentIndex] === planet.name
            || !selectedPlanets.includes(planet.name),
    );
    const list = planetList.map((data, index) => (
      <option value={data.name}>{data.name}</option>
    ));


    return list;
  };
  function handleChange(value:string) {
    console.log(`selected ${value}`);
  }

  return (
    <Select defaultValue="lucy" style={{ width: 120 }} onChange={handleChange}>
      <Option value={props.index}>{props.index}</Option>
      {planetOptions()}
    </Select>
  );
};
export default SelectPlanet;
