import React, { useEffect, useState, ChangeEvent } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Select } from 'antd';
import { Radio , Input} from 'antd';

import * as actions from "../actions";
import {
  thunkSendMessage,
  planetsApi,
  VehiclesApi,
  GetTokenFromApi,
} from "../actions/thunkActions";
import logo from "../logo.svg";
import "./App.css";
import { State } from "../reducers";
import { Planet } from "../types/types";
const { Option } = Select;

const radioStyle = {
  display: 'block',
  height: '30px',
  lineHeight: '30px',
};
const Selection = () => {
  const message = useSelector((state: State) => state.message);
  const id = useSelector((state: State) => state.id);
  const allPlanets = useSelector((state: State) => state.planet);
  const allVehicles = useSelector((state: State) => state.vehicle);
  const dispatch = useDispatch();

  const [selectedPlanets, setPlanets] = useState<Array<string>>([]);
  const [planetArray, setArray] = useState<Planet[]>([]);

  useEffect(() => {
    dispatch(GetTokenFromApi());
    dispatch(planetsApi());
    dispatch(VehiclesApi());
  }, []);

  const onClick = () => {
    dispatch(actions.sampleAction2(id + 1));
    dispatch(thunkSendMessage("hello"));
  };
  const onSelectPlanet = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedPlanet = event.currentTarget.value;
    const planetDistance = allPlanets.filter(
      (planet) => planet.name === selectedPlanet
    );
    console.log("planetDistance", planetDistance);
    const index = Number(event.currentTarget.id);
    planetArray[index] = planetDistance[0];
    setArray([...planetArray]);
    const vehilceList =listVehiclesAt(index);
    console.log("vehicleList::",vehilceList);
    console.log("PlanetArray", planetArray, index, selectedPlanet);
    selectedPlanets[index] = selectedPlanet;
    setPlanets([...selectedPlanets]);
    console.log("onSelectPlanet::", selectedPlanets);
    return vehilceList;

  };

  const listPlanetOptionsAt = (currentIndex: number) => {
    const planetList = allPlanets.filter(
      (planet) =>
        selectedPlanets[currentIndex] === planet.name ||
        !selectedPlanets.includes(planet.name)
    );
    const list = planetList.map((data, index) => (
      <option value={data.name}>{data.name}</option>
    ));


    console.log("showPlanets::", planetList);
    return list;


  };

  const listVehiclesAt = (currentIndex: number) => {
    const distance: number = planetArray[currentIndex].distance ;

    const vehicleList = allVehicles.filter(
      (vehicle) => vehicle.max_distance >= distance
    );
    const vehicleOptions = vehicleList.map((vehicle) => (
      <>

<Radio.Group onChange={this.onChange} value={1}>
        <Radio style={radioStyle} value={1}>
          Option A
        </Radio>
</Radio.Group>


      </>
    ));
    return vehicleOptions;




  };






  return (
    <div>
      <div className='App'>
        <header className='App-header'>
          message: {message}
          <img src={logo} className='App-logo' alt='logo' />
          id: {id}
          <button type='button' onClick={onClick}>
            increment id
          </button>
          <div className='row'>
            <div className='col'>
              <select
                id='0'
                value={selectedPlanets[0]}
                onChange={onSelectPlanet}
              >
                <option></option>

                {listPlanetOptionsAt(0)}
              </select>
              {
                  selectedPlanets[0] === undefined ? "" : listVehiclesAt(0)
                }


            </div>
            <div className='col'>

              <select
                value={selectedPlanets[1]}
                id='1'
                onChange={onSelectPlanet}
              >
                <option></option>
                {listPlanetOptionsAt(1)}
              </select>
              {
                  selectedPlanets[1] === undefined ? "" : listVehiclesAt(1)
                }
            </div>
            <div className='col'>
              <select
                value={selectedPlanets[2]}
                id='2'
                onChange={onSelectPlanet}
              >
                <option></option>
                {listPlanetOptionsAt(2)}
              </select>
              {
                  selectedPlanets[2] === undefined ? "" : listVehiclesAt(2)
                }
            </div>
            <div className='col'>
              <select
                value={selectedPlanets[3]}
                id='3'
                onChange={onSelectPlanet}
              >
                <option></option>
                {listPlanetOptionsAt(3)}
              </select>
              {
                  selectedPlanets[3] === undefined ? "" : listVehiclesAt(3)
                }
            </div>
          </div>

        </header>
      </div>
    </div>
  );
};
export default Selection;


